import nodeFetch from 'node-fetch';
const isServer = true;
const GQ_ENDPOINT = isServer ? `http://localhost:${8081}/graphql` : '/graphql';
export default (async request => {
  const fetcher = isServer ? nodeFetch : fetch;
  const promise = fetcher(GQ_ENDPOINT, {
    method: 'POST',
    body: request,
    headers: {
      "Content-Type": "application/graphql"
    }
  });
  const result = await promise.then(e => {
    return e.json();
  });

  if (!result || result.errors) {
    console.log('Error while fetching GQ', {
      result
    });
    return {};
  }

  return result.data;
});
